from typing import List
from collections import defaultdict
import math

class Solution:
    def countBadPairs(self, nums: List[int]) -> int:
        # 按照题目意思进行模拟:O(N^2)
        n = len(nums)
        c = 0
        for i in range(n-1):
            for j in range(i+1, n):
                if j - i != nums[j] - nums[i]:
                    c += 1
        return c
    
    def countBadPairs2(self, nums: List[int]) -> int:
        # 题目要求 j - i != nums[j] - nums[i]
        # 移项得到 nums[i] - i != nums[j] - j
        # 正难则反：用总的组合的数目 n*(n-1)//2 减去 nums[i] - i == nums[j] - j 的数对数目即可
        # 设 a[i] = nums[i] - i，就和 No.1521 一样了
        # py 中对于从 n 个元素中取 k 个这种 Cnk 的组合计算可以使用 math.comb(n, k) 函数
        n = len(nums)
        freq = defaultdict(int)
        good_pairs = 0
        for i, num in enumerate(nums):
            good_pairs += freq[num - i]
            freq[num - i] += 1
        # return n*(n-1)//2 - good_pairs    
        return math.comb(n, 2) - good_pairs

    

